import { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./Chat.css";

const Chat = () => {
  const [message, setMessage] = useState("");
  const [showMenu, setShowMenu] = useState(false);
  const token = localStorage.getItem("token");
  const navigate = useNavigate();

  const sendMessage = async (e) => {
    e.preventDefault();
    if (!message.trim()) return;

    try {
      const res = await axios.post(
        "http://localhost:5000/api/chat/send-message",
        { message },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setMessage("");
    } catch (error) {
      console.error("Failed to send message", error);
    }
  };

  const clearChatHistory = async () => {
    try {
      await axios.delete("http://localhost:5000/api/chat/clear-history", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setShowMenu(false);
    } catch (error) {
      console.error("Failed to clear chat history", error);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("userId");
    navigate("/");
  };

  return (
    <div className="chat-container">
      <div className="chat-header">
        <h2>Chat with AI</h2>
        <div className="profile-icon" onClick={() => setShowMenu(!showMenu)}>
          ðŸ‘¤
          {showMenu && (
            <div className="menu">
              <button onClick={() => navigate("/profile")}>Profile</button>
              <button onClick={clearChatHistory}>Clear History</button>
              <button onClick={handleLogout}>Logout</button>
            </div>
          )}
        </div>
      </div>

      <form className="chat-form" onSubmit={sendMessage}>
        <input
          type="text"
          placeholder="Type a message..."
          value={message}
          onChange={(e) => setMessage(e.target.value)}
        />
        <button type="submit">Send</button>
      </form>

      <button onClick={() => navigate("/chat-history")} className="view-history-button">
        View Chat History
      </button>
    </div>
  );
};

export default Chat;
