import { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("http://localhost:5000/api/auth/login", { email, password });

      localStorage.setItem("token", res.data.token);
      localStorage.setItem("userId", res.data.user.id); // ðŸ”¹ Store userId for logout
      navigate("/chat");
    } catch (error) {
      alert("Login Failed");
    }
  };

  const handleRegisterClick = () => {
    navigate("/register"); // ðŸ”¹ Navigate to the register page
  };

  return (
    <div>
      <h2>Login</h2>
      <form onSubmit={handleLogin}>
        <input type="email" placeholder="Email" onChange={(e) => setEmail(e.target.value)} />
        <input type="password" placeholder="Password" onChange={(e) => setPassword(e.target.value)} />
        <button type="submit">Laogin</button>
      </form>
      <button onClick={handleRegisterClick}>Register</button> {/* ðŸ”¹ Register button */}
    </div>
  );
};

export default Login;
