import { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./Chat.css"; // Import the CSS file

const Chat = () => {
  // State variables
  const [message, setMessage] = useState(""); // Holds the current message input
  const [chatHistory, setChatHistory] = useState([]); // Stores the chat history
  const [showMenu, setShowMenu] = useState(false); // Controls visibility of the profile menu
  const token = localStorage.getItem("token"); // Retrieves the user token from localStorage
  const navigate = useNavigate(); // Hook for navigation

  // Fetch chat history on component mount
  useEffect(() => {
    fetchChatHistory();
  }, []);

  // Fetches chat history from the server
  const fetchChatHistory = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/chat/get-history", {
        headers: { Authorization: `Bearer ${token}` },
      });
      // Set chat history, or a default message if history is empty
      setChatHistory(
        response.data.length > 0
          ? response.data
          : [{ role: "assistant", content: "Start chatting with AI!" }]
      );
    } catch (error) {
      console.error("Failed to load chat history", error);
    }
  };

  // Sends a message to the server and updates chat history
  const sendMessage = async (e) => {
    e.preventDefault();
    if (!message.trim()) return; // Ignore empty messages

    // Add user message to chat history
    setChatHistory((prev) => [...prev, { role: "user", content: message }]);
    setMessage(""); // Clear the input field

    try {
      // Send message to the server
      const response = await axios.post(
        "http://localhost:5000/api/chat/send-message",
        { message },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      // Add assistant's reply to chat history
      setChatHistory((prev) => [...prev, { role: "assistant", content: response.data.reply }]);
    } catch (error) {
      console.error("Failed to send message", error);
    }
  };

  // Clears the chat history
  const clearChatHistory = async () => {
    try {
      await axios.delete("http://localhost:5000/api/chat/clear-history", {
        headers: { Authorization: `Bearer ${token}` },
      });
      // Reset chat history to default message
      setChatHistory([{ role: "assistant", content: "Start chatting with AI!" }]);
      setShowMenu(false); // Hide the menu after clearing history
    } catch (error) {
      console.error("Failed to clear chat history", error);
    }
  };

  // Handles user logout
  const handleLogout = () => {
    localStorage.removeItem("token"); // Remove token from localStorage
    localStorage.removeItem("userId"); // Remove userId from localStorage
    navigate("/"); // Navigate to the login page
  };

  return (
    <div className="chat-container">
      {/* Header section with profile icon and menu */}
      <div className="chat-header">
        <h2>Chat with AI</h2>
        <div className="profile-icon" onClick={() => setShowMenu(!showMenu)}>
          👤
          {/* Dropdown menu */}
          {showMenu && (
            <div className="menu">
              <button onClick={() => navigate("/profile")}>Profile</button>
              <button onClick={clearChatHistory}>Clear History</button>
              <button onClick={handleLogout}>Logout</button>
            </div>
          )}
        </div>
      </div>

      {/* Chat history display */}
      <div className="chat-history">
        {chatHistory.map((msg, index) => (
          <div
            key={index}
            className={`message ${msg.role === "user" ? "user-message" : "assistant-message"}`}
          >
            <strong>{msg.role}:</strong> {msg.content}
          </div>
        ))}
      </div>

      {/* Message input form */}
      <form className="chat-form" onSubmit={sendMessage}>
        <input
          type="text"
          placeholder="Type a message..."
          value={message}
          onChange={(e) => setMessage(e.target.value)}
        />
        <button type="submit">Send</button>
      </form>
    </div>
  );
};

export default Chat;
